export function getAuthToken() {
  const token = localStorage.getItem("token");
  return token;
}

// 토큰 정보 얻는 함수를 loader()로 감싸기
export function loader() {
  return getAuthToken();
}

export function checkAuthLoader() {
  const token = getAuthToken();

  if (!token) {
    return redirect('/auth')
  }
}
